using System; namespace Snoopy.Data.Model{	public class food_recommendation	{		public int ID{ get; set; }		public string user_id{ get; set; }		public string serial_code{ get; set; }		public int food_id{ get; set; }		public int recipe_id{ get; set; }		/// <summary>		/// 推荐食用的日期		/// </summary>		public DateTime reco_date{ get; set; }		/// <summary>		/// 推荐食用的时长，按天计		/// </summary>		public int reco_period{ get; set; }		/// <summary>		/// 食用频度，每周几次		/// </summary>		public int reco_frequency{ get; set; }		/// <summary>		/// 时间戳		/// </summary>		public DateTime add_date{ get; set; }	}}