using System; namespace Snoopy.Data.Model{	public class note_box	{		public int box_id{ get; set; }		public string box_name{ get; set; }		/// <summary>		/// 0表示日志类型；1表示单次计划；2表示循环计划		/// </summary>		public int box_type{ get; set; }		public string box_plan_frequency{ get; set; }		/// <summary>		/// h:hourly; d:daily; w:weekly; m:monthly		/// </summary>		public string box_plan_frequency_unit{ get; set; }		/// <summary>		/// 非循环计划，计划时间		/// </summary>		public DateTime box_plan_time{ get; set; }		public DateTime time_stamp{ get; set; }	}}